mapper.select:
SELECT basket_no, user_no, class_no
FROM basket
WHERE user_no = #{userNo}

SELECT class_no, teacher, class_name, class_start, class_end, max_count, expense, class_info, on_off
FROM class
WHERE class_no = #{classNo}

mapper.delete:
		DELETE FROM basket
  		WHERE user_no = #{userNo}
  			AND class_no = #{classNo}
